function Phylotree(props) {
  const [tooltip, setTooltip] = useState(false);
  const { width, height, maxLabelWidth } = props;
  const [isOpen, setIsOpen] = useState(false);
  const container = useRef();

  useEffect (()=>{
    window.addEventListener("mousedown", handleClickOutside);
    return () => {
    window.removeEventListener("mousedown", handleClickOutside);
    }
  },[])
  const handleClickOutside = (event) => {
    if (
      container.current &&
      !container.current.contains(event.target)
    ) {
      setIsOpen(false);
    }
  };
  var{ tree, newick } = props;
  if (!tree && !newick) {
    return <g />;
  } else if(!tree) {
    tree = new phylotree(newick);
    
  }
  if(tree && props.reroot) {
    var node = props.reroot;
    if (node.parent) {

      var new_json = d3__namespace.hierarchy({
        name: "new_root"
      });
      
      new_json.children = [node.copy()];
      new_json.data.__mapped_bl = undefined;

      nodes.each(n => {
        n.data.__mapped_bl = tree.branch_length_accessor(n);
      });

      tree.setBranchLength(n => {
        return n.data.__mapped_bl;
      });

      let remove_me = node,
        current_node = node.parent,
        stashed_bl = ___namespace.noop();

      let apportioned_bl =
        node.data.__mapped_bl === undefined ? undefined : node.data.__mapped_bl * fraction;

      stashed_bl = current_node.data.__mapped_bl;

      current_node.data.__mapped_bl =
        node.data.__mapped_bl === undefined
          ? undefined
          : node.data.__mapped_bl - apportioned_bl;

      node.data.__mapped_bl = apportioned_bl;

      var remove_idx;

      if (current_node.parent) {

        new_json.children.push(current_node);

        while (current_node.parent) {
          remove_idx = current_node.children.indexOf(remove_me);
          if (current_node.parent.parent) {
            current_node.children.splice(remove_idx, 1, current_node.parent);
          } else {
            current_node.children.splice(remove_idx, 1);
          }

          let t = current_node.parent.data.__mapped_bl;

          if (t !== undefined) {
            current_node.parent.data.__mapped_bl = stashed_bl;
            stashed_bl = t;
          }
          remove_me = current_node;
          current_node = current_node.parent;
        }
        // remove_idx = current_node.children.indexOf(remove_me);
        // current_node.children.splice(remove_idx, 1);
      } else {
        // remove_idx = current_node.children.indexOf(remove_me);
        // current_node.children.splice(remove_idx, 1);
        stashed_bl = current_node.data.__mapped_bl;
        remove_me = new_json;
      }

      // current_node is now old root, and remove_me is the root child we came up
      // the tree through
      if (current_node.children.length == 1) {
        if (stashed_bl) {
          current_node.children[0].data.__mapped_bl += stashed_bl;
        }
        remove_me.children = remove_me.children.concat(current_node.children);
      } else {
        let new_node = new d3__namespace.hierarchy({ name: "__reroot_top_clade", __mapped_bl: stashed_bl });
        ___namespace.extendOwn (new_json.children[0], node);
        new_node.data.__mapped_bl = stashed_bl;
        new_node.children = current_node.children.map(function(n) {
          n.parent = new_node;
          return n;
        });

        new_node.parent = remove_me;
        remove_me.children.push(new_node);
       }
      console.log(new_node)
    }
     }
  
  
    if(!props.skipPlacement ) {
    placenodes(tree, props.internalNodeLabels, props.accessor, props.sort); 
  }
  function attachTextWidth(node) {
    node.data.text_width = text_width(node.data.name, 12, maxLabelWidth);
    if(node.children) node.children.forEach(attachTextWidth);
  }
  attachTextWidth(tree.nodes);
  const sorted_tips = tree.getTips().sort((a,b) => (
      b.data.abstract_x - a.data.abstract_x
    ));
  var rightmost;
 
    for(let i=0; i < sorted_tips.length; i++) {
      let tip = sorted_tips[i];
      rightmost = width - tip.data.text_width;
      let scale = rightmost / tip.data.abstract_x;
      let none_cross = sorted_tips.map(tip => {
        const tip_x = tip.data.abstract_x * scale,
          text_x = width - tip.data.text_width,
          this_doesnt_cross = Math.floor(tip_x) < Math.ceil(text_x);
        return this_doesnt_cross;
      }).every(x => x);
      if(none_cross) break;
   
  }
  const x_scale = scaleLinear()
      .domain([0, tree.max_x])
      .range([0, rightmost]),
    y_scale = scaleLinear()
      .domain([0, tree.max_y])
      .range([props.includeBLAxis ? 60 : 0, height]),
    color_scale = getColorScale(tree, props.highlightBranches);
  

  return (
  <div class="row" class= "col-md-12">
    <SVG width={width+100} height={height+100} >
   
  
  <g transform={props.transform}>
  <defs></defs>
    {/* {props.includeBLAxis ? <g>
      <text
        x={x_scale(tree.max_x/2)}
        y={10}
        alignmentBaseline='middle'
        textAnchor='middle'
        fontFamily='Courier'
      >
        Substitutions per site
      </text>
      <AxisTop
        transform={`translate(0, 40)`}
        scale={x_scale}
      />
    </g> : null } */}
    
    {tree.links.map(link => {
      const source_id = link.source.unique_id,
        target_id = link.target.unique_id,
        key = source_id + "," + target_id,
        show_label = props.internalNodeLabels ||
          (props.showLabels && tree.isLeafNode(link.target));
      const  show_attribute = props.showAttributes;
      const show_value1 = props.showValue1;
      const show_value2 = props.showValue2;
      const show_value3 = props.showValue3;
      const show_value4 = props.showValue4;
      const show_value5 = props.showValue5;
      return(
      <Branch
        tree={tree}
        key={key}
        xScale={x_scale}
        yScale={y_scale}
        colorScale={color_scale}
        link={link}
        showLabel={show_label}
        showAttribute={show_attribute}
        showValue1={show_value1}
        showValue2={show_value2}
        showValue3={show_value3}
        showValue4={show_value4}
        showValue5={show_value5}
        maxLabelWidth={maxLabelWidth}
        width={width}
        alignTips={props.alignTips}
        branchStyler={props.branchStyler}
        labelStyler={props.labelStyler}
        tooltip={props.tooltip}
        setTooltip={setTooltip}
        setIsOpen={setIsOpen}
        isOpen ={props.isOpen}
      />
     );
    }) } 
    
    { tooltip ? (<props.tooltip
      width={props.width}
      height={props.height}
      {...tooltip}
    />
    ) : null }
  </g>
  </SVG>
  <div ref={container}>
  { isOpen ? (
    <props.isOpen 
    {...isOpen}
    />
  ) : null}
  </div>
  </div>
  );
}

Phylotree.defaultProps = {
  width: 500,
  height: 500,
  showLabels: true,
  showAttributes: false,
  showValue1: false,
  showValue2: false,
  showValue3: false,
  showValue4: false,
  showValue5: false,
  skipPlacement: false,
  maxLabelWidth: 20,
  alignTips: "right",
  accessor: default_accessor,
  branchStyler: null,
  labelStyler: null,
  tooltip: null,
  isOpen: null,
  sort: null,
  reroot: null,
  includeBLAxis: true
};

export default Phylotree;
export {
  placenodes
};
